from tkinter import *
from tkinter import messagebox
import os

dict1 = {'q': '/', 'w': "'", 'e':'ק', 'r':'ר', 't':'א', 'y':'ט', 'u':'ו', 'i':'ן', 'o':'ם', 'p':'פ', 'a':'ש',
     's':'ד', 'd':'ג', 'f':'כ', 'g':'ע', 'h':'י', 'j':'ח', 'k':'ל', 'l':'ך', ';':'ף',
      'z':'ז', 'x':'ס', 'c':'ב', 'v':'ה', 'b':'נ', 'n':'מ', 'm':'צ', ',':'ת', '.':'ץ'}
dict2 = {}
for key, value in dict1.items():
    dict2.update({value: key})
def keyHeToEn(text):
    convert = []
    
    for i in text:
        if i not in dict2: convert.append(i)
        else: convert.append(dict2[i])
    return (''.join(str(e) for e in convert))

def keyEnToHe(text):
    convert = []
    
    for i in text:
        if i not in dict1: convert.append(i)
        else: convert.append(dict1[i])
    return (''.join(str(e) for e in convert))
    
class window(Frame):
    def __init__(self, master=None):
        super().__init__(master)
        self.master = master
        self.var = IntVar()
        self.Text = StringVar()
        self.Result = StringVar()
        self.makewindow()

    def addToClipBoard(self):
        text = self.Result.get()  
        print(text)
        command = 'echo ' + text.strip() + '| clip'
        os.system(command)
        messagebox.showinfo(title="info", message="The result has copy to the clipboard!")
    
    def StartConvert(self):
        if self.var.get() == 1: self.Result.set(keyHeToEn(self.Text.get()))
        elif self.var.get() == 2: self.Result.set(keyEnToHe(self.Text.get()))
        else: messagebox.showerror(title="ERROR", message="PLS CHOOSE THE CONVERT TYPE")
    
    def makewindow(self):     
        Radiobutton(self.master, text="He To En", font = ("David", 15), variable=self.var, value=1).pack()
        Radiobutton(self.master, text="En To He", font = ("David", 15), variable=self.var, value=2).pack()
        Label(self.master, textvariable=self.Result, width=70).pack(pady=5)
        Entry(self.master, textvariable=self.Text, width=60, bd = 3).pack()
        Button(self.master, text="convert", font = ("David", 15), width=32, command=self.StartConvert).pack(pady=5)
        Button(self.master, text="copy to clipboard", font = ("David", 15), width=32, command=self.addToClipBoard).pack(pady=5)

if __name__ == "__main__":
    win = Tk(className='convertKeyboard')
    icon_img_dot_png_in_base64 = ""
    photo = PhotoImage(data=icon_img_dot_png_in_base64)
    #photo = PhotoImage(file="icon.png")
    win.wm_iconphoto(False, photo)
    w = window(win)
    win.mainloop()
